import { createReducer } from "../../app/common/util/reducerUtils";
import { CREATE_EVENT, UPDATE_EVENT, DELETE_EVENT } from "./eventConstants";

const initialState = [
  {
    id: '1',
    title: 'Art at FACS Mod',
    date: '2018-03-27',
    category: 'art',
    description: 'Do art at FACS Mod',
    city: 'FACS MOD',
    venue: 'Crafting, Drawing, Designing',
    hostedBy: 'Bob',
    hostPhotoURL: 'https://randomuser.me/api/portraits/men/20.jpg',
    attendees: [
      {
        id: 'a',
        name: 'Bob',
        photoURL: 'https://randomuser.me/api/portraits/men/20.jpg'
      },
      {
        id: 'b',
        name: 'Tom',
        photoURL: 'https://randomuser.me/api/portraits/men/22.jpg'
      }
    ]
  },
  {
    id: '2',
    title: 'Math tutoring at A12',
    date: '2018-03-28',
    category: 'teach',
    description: 'Get tutored peer to peer at A12',
    city: 'A12',
    venue: 'Math 6/7, Math 8, Algreba 1',
    hostedBy: 'Tom',
    hostPhotoURL: 'https://randomuser.me/api/portraits/men/22.jpg',
    attendees: [
      {
        id: 'b',
        name: 'Tom',
        photoURL: 'https://randomuser.me/api/portraits/men/22.jpg'
      },
      {
        id: 'a',
        name: 'Bob',
        photoURL: 'https://randomuser.me/api/portraits/men/20.jpg'
      }
    ]
  }
];

const createEvent = (state, payload) => {
    return[...state, payload.event]
}

const updateEvent = (state, payload) => {
    return[
        ...state.filter(event => event.id !== payload.event.id), payload.event
    ]
}

const deleteEvent = (state, payload) => {
    return[
        ...state.filter(event => event.id !== payload.event.id), payload.event
    ]
}


export default createReducer(initialState, {
    [CREATE_EVENT]: createEvent,
    [UPDATE_EVENT]: updateEvent,
    [DELETE_EVENT]: deleteEvent
})
